sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

// Query the database for Cloud Service Task records with Due date field values within
// 48 hours from now.  Only return Cloud Service Task records that do not have
// a Status field value of Complete.
var dueSoon = new GlideRecord('x_828747_cloud_s_1_cloud_main');

if(dueSoon) gs.info("Record Instantiated"); else gs.info("Record failed to Instantiate");

//dueSoon.addQuery('due_date','>=',rightNow);
//dueSoon.addQuery('due_date','<', gs.hoursAgo(-48));
dueSoon.addQuery('status','!=','complete');
dueSoon.addQuery('due_date');

dueSoon.query();
// Write a log message for each Cloud Service Task Record

while(dueSoon.next()){
    gs.info("Cloud Service Task due soon record = " + dueSoon.number);
}
